import React from 'react'
import { Redirect, Route } from 'react-router-dom'
import {isLogin} from './SignIn';

class PrivateRouters extends React.Component {

    render() {
        const Component = this.props.component;
       
        return isLogin ? (
            <Component />
        ) : (
            <Redirect to={{ pathname: '/SignIn' }} />
        );
    }
}
