// }
    export const isLogin = () =>{
        if(localStorage.getItem("userData")){
            return true;
        }
    }
const PrivateRouters = ({component: Component, ...rest}) => {
    return(
        <Route {...rest} render={props => (
            isLogin() ?
                <Component {...props} />
            : <Redirect to="/signin" />
        )} />
    )
}

export default PrivateRouters;
