export function Postdata(type, userData){
    let BaseUrl = 'https://api.flippay.co/auth/';

    return new Promise((resolve, reject) =>{
        fetch(BaseUrl+type,{
            method: 'POST',
            body: JSON.stringify(userData)
        })
        .then((response) => response.json())
        .then((responseJson)=> {
            resolve(responseJson);
        })
        .catch((error) => {
            reject(error);
        });
    });
  }
