import React, { Component } from 'react';
import { MDBContainer, MDBRow, MDBCol,MDBInput, MDBBtn } from "mdbreact";
import { Postdata } from './Postdata';
import './signInStyle.css';
import { Link } from 'react-router-dom';

 class signIn extends Component {
   
    constructor(props){
        super(props);
        
        this.state={
            email:'',
            password:''
        }
        this.login =this.login.bind(this);
        this.onChange = this.onChange.bind(this);
    }
    login(){
        Postdata('login', this.state).then((result) =>{
            let responseJSON = result;
            console.log(responseJSON);
        });
}


    onChange(e){
        this.setState({[e.target.name]:e.target.value})
    }
    render() {
        return (
            <div>
                        <MDBContainer fluid className="contain">
            <MDBRow>
            <MDBCol size="6" className="form-style" sm="6" md="5" lg="6">
            <div className="form-wrapper">
            <div className="signIn-form">
                <h1 className="h5 text-center mb-4">Sign In to Flippay</h1>
                <p>Use your credentials to login into account.</p>
                <div className="grey-text">
                <div className="textbox"> 
                <i class="fas fa-user"  aria-hidden="true"></i>
                <input type="text" name="email" placeholder="email" value="" onChange={this.onChange}/>
                </div>
                <div className="textbox"> 
                <i class="fas fa-lock" aria-hidden="true"></i>
                <input type="password" name="password" placeholder="password" value="" onChange={this.onChange}/>
                </div>
                </div>
                <div className="text-center">
                <Link to="/SignUp">Use your credentials to create account.</Link>
                <MDBBtn type="submit" value="login" className="button" onClick={this.login} className="form-btn">Login</MDBBtn>
                {/* <input type="submit" value="login" className="button" onClick={this.login}/> */}
                </div>
            </div>
            </div>
            </MDBCol>
            <MDBCol size="6" sm="6" md="5" lg="6" className="row-no-padding">
                <div className="img-styler">
                    <div className="overLay">
                        <h2>Hello There! Join Us</h2>
                        <p>Lorem dolore elit cillum nostrud ex in minim et.</p>
                        <h1>button</h1>
                    </div>
                </div>
            </MDBCol>
          </MDBRow>
        </MDBContainer>
             {/* <div>
                 <h2>Login Page</h2>
                 <label>Email</label>
                 <input type="text" name="email" placeholder="email" onChange={this.onChange}/>
                 <label>Password</label>
                <input type="password" name="password" placeholder="password" onChange={this.onChange}/>
                 <input type="submit" value="login" className="button" onClick={this.login}/>
                  </div> */}
            </div> 
        )
    }
}
export default signIn;
