import React from 'react';
import { Route, Redirect} from 'react-router-dom';
export const PrivateRoute = ({ component: Component, ...rest }) => {
  return (
    <Route
      {...rest}
      render={props =>
        localStorage.getItem('token') ? (
          <Component {...props} />
        ) : (
          <Redirect
            to={{
              pathname: "/SignIn",
              state: { from: props.location }
            }}
          />
        )
      }
    />
  );
};
