export default class dashboard extends Component {
    constructor(props){
        super(props);
        this.state = {
            redirect:false
        }
    }

    componentWillMount(){
        if(localStorage.getItem("userData")){
            console.log("call user feed");
        }
        else{
            this.setState({redirect: true})
        }
    }
