import React, { Component } from 'react';
import logo from "../../assets/mdb-react.png";
import { MDBListGroup, MDBListGroupItem, MDBIcon } from 'mdbreact';
import { NavLink } from 'react-router-dom';
import "./sideNav.css";
import "./dash.css";


const url = 'https://api.flippay.co/user/user';
class TopNavigation extends Component{

    constructor(props) {
        super(props);
        this.state = {
          isLoaded: false,
          items: [],
        };

       this.getData = this.getData.bind(this);
      }
      componentDidMount() {
        this.getData();
      }
      getData(){
        let auth =  localStorage.getItem("userData");
              return fetch(url, {
               method: 'GET',
               headers:{
                 Accept: 'application/json',
                          'Content-Type': 'application/json',
                          'Authorization': "Bearer " + auth,

                  },

            //  }).then((user)=> {
            //      user.json().then((resp => {

            //          this.setState({data:resp})
            //      }))
             })
                //  .then(res => res.json())
                //  .then(
                //     (person) => {

                //       this.setState({
                //         isLoaded: true,
                //         items: person.user
                //       });
                //     },
                //     (error) => {
                //       this.setState({
                //         isLoaded: true,
                //         error
                //       });
                //     }
                //   )

              }
    render(){
        // const data= this.state.data;
        // console.warn(data);
        return (
            <div className="sidebar-fixed position-fixed" id="background-logo" color="primary-color-dark">
                <div>
                    <a href="#!" className="logo-wrapper waves-effect">
                        <img alt="MDB React Logo" className="img-fluid" src='/images/IMG-20200317-WA0134-removebg.png'/>
                    </a>
                </div>

                <div className="user-circle">
                    <img
                    src="https://mdbootstrap.com/img/Photos/Avatars/img%20(9).jpg"
                    alt=""
                    className="rounded-circle img-fluid userImg"
                    />
                    <p style={{color: "white"}}>Test User</p>
                    <p style={{color: "white", marginTop: "-10px"}}>Testing@test.com</p>
                    <div>
                        {/* {this.state.isLoaded || !this.state.items ? (
                            <div>loading...</div>
                        ) : (
                            <div>
                                <div>{this.state.items.email}</div>
                            </div>
                        )} */}
                    </div>
                </div>

                <div className="sideStyle">
                    <MDBListGroup className="list-group-flush">
                        <NavLink exact={true} to="/" activeClassName="activeClass">
                            <MDBListGroupItem>
                                <MDBIcon icon="chart-pie" className="mr-3"/>
                                Dashboard
                            </MDBListGroupItem>
                        </NavLink>
                        <NavLink to="/Wallet" activeClassName="activeClass">
                            <MDBListGroupItem>
                                <MDBIcon icon="user" className="mr-3"/>
                                Wallet
                            </MDBListGroupItem>
                        </NavLink>
                        <NavLink to="/tables" activeClassName="activeClass listen">
                            <MDBListGroupItem>
                                <MDBIcon icon="table" className="mr-3"/>
                                Lorem
                            </MDBListGroupItem>
                        </NavLink>
                        <NavLink to="/maps" activeClassName="activeClass">
                            <MDBListGroupItem>
                                <MDBIcon icon="map" className="mr-3"/>
                                Lorem
                            </MDBListGroupItem>
                        </NavLink>
                        <NavLink to="/404" activeClassName="activeClass">
                            <MDBListGroupItem>
                                <MDBIcon icon="exclamation" className="mr-3"/>
                                404
                            </MDBListGroupItem>
                        </NavLink>
                    </MDBListGroup>
                </div>
            </div>
        );
    }
}

export default TopNavigation;
