import React, { Component } from 'react';
import FormField from '../widgets/formFields/formFields';

export default class signIn extends Component {
    
    state ={
        registerError:'',
        loading:false,
        formdata:{
            email:{
                element:'input',
                value:'',
                config:{
                    name:'email_input',
                    type:'email',
                    placeholder:'Enter your email'
                },
                validation:{
                    required:true,
                    email:true
                },
                valid:false,
                touched:false,
                validationMessage:''
            },
            password:{
                element:'input',
                value:'',
                config:{
                    name:'password_input',
                    type:'password',
                    placeholder:'Enter your password'
                },
                validation:{
                    required:true,
                    password:true
                },
                valid:false,
                touched:false,
                validationMessage:''
            }
        }
    }
    
    updateForm = (element) => {
        // const newFormdata = {
        //     ...this.state.formdata
        // }
        // const newElement = {
        //     ...newFormdata[element.id]
        // }
        // newElement.value = element.event.target.value;
        // newFormdata[element.id]= newElement;

        // this.setState({
        //     formdata:newFormdata
        // })
        console.log(element)
    }
    
    
    
    
    render() {
        return (
            <div>
            <FormField
                 id={'email'}
                 formdata={this.state.formdata.email}
                 change={(element)=> this.updateForm(element)}
            />
            <FormField
                 id={'password'}
                 forminfo={this.state.formdata.password}
                 valueChange={(element)=> this.updateForm(element)}
            />
            </div>
        )
    }
}
